// frontend/src/pages/Profile.js
import React, { useEffect, useState } from 'react';
import api from '../services/api';

const Profile = () => {
    const [loans, setLoans] = useState([]);

    useEffect(() => {
        const fetchLoans = async () => {
            try {
                const res = await api.get('/transactions/my-loans');
                setLoans(res.data);
            } catch (err) {
                console.error("Error fetching loans");
            }
        };
        fetchLoans();
    }, []);

    // frontend/src/pages/Profile.js


    
// frontend/src/pages/Profile.js
const handleReturn = async (transactionId, bookId) => {
    console.log("SENDING TO BACKEND:", { transactionId, bookId }); // LOG THIS
    try {
        await api.post('/transactions/return', { transactionId, bookId });
        // ... success logic
    } catch (err) {
        console.error("RETURN API ERROR:", err.response?.data);
    }
};

    return (
        <div style={{ padding: '20px' }}>
            <h2>My Borrowed Books</h2>
            {loans.length === 0 ? <p>You haven't borrowed any books yet.</p> : (
                <table style={{ width: '100%', textAlign: 'left', borderCollapse: 'collapse' }}>
                    <thead>
                        <tr style={{ borderBottom: '2px solid #ccc' }}>
                            <th>Title</th>
                            <th>Author</th>
                            <th>Issue Date</th>
                        </tr>
                    </thead>
                    <tbody>


{loans.map(loan => (
    <tr key={loan.id}>
        <td>{loan.book_name}</td>
        <td>
            <button onClick={() => handleReturn(loan.id, loan.book_id)}>
    Return Book
</button>
        </td>
    </tr>
))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Profile;
