import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <nav style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem', background: '#333', color: '#fff' }}>
      <h2>ðŸ“š LMS</h2>
      <div>
        {token ? (
          <>
            <Link to="/books" style={{ color: '#fff', margin: '0 10px' }}>Books</Link>
            <Link to="/profile" style={{ color: '#fff', margin: '0 10px' }}>My Loans</Link>
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" style={{ color: '#fff', margin: '0 10px' }}>Login</Link>
            <Link to="/register" style={{ color: '#fff', margin: '0 10px' }}>Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
