import React, { useState } from 'react';
import axios from 'axios';

const AddBookForm = () => {
    const [book, setBook] = useState({ title: '', author: '', isbn: '', copies: 1 });

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/api/admin/add-book', book, {
                headers: { 'x-auth-token': localStorage.getItem('token') }
            });
            alert("Book added successfully!");
            setBook({ title: '', author: '', isbn: '', copies: 1 });
        } catch (err) {
            alert("Error adding book");
        }
    };

    return (
        <div style={{ maxWidth: '500px', margin: '0 auto', background: 'white', padding: '20px', borderRadius: '8px' }}>
            <h3>Add New Book to Library</h3>
            <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
                <input type="text" placeholder="Book Title" value={book.title} onChange={(e) => setBook({...book, title: e.target.value})} required />
                <input type="text" placeholder="Author" value={book.author} onChange={(e) => setBook({...book, author: e.target.value})} required />
                <input type="text" placeholder="ISBN" value={book.isbn} onChange={(e) => setBook({...book, isbn: e.target.value})} />
                <input type="number" placeholder="Copies" value={book.copies} onChange={(e) => setBook({...book, copies: e.target.value})} required />
                <button type="submit" style={{ backgroundColor: '#27ae60', color: 'white', padding: '10px', border: 'none', borderRadius: '5px' }}>Save Book</button>
            </form>
        </div>
    );
};

export default AddBookForm;
