// frontend/src/components/Navbar.js
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');
  const userRole = localStorage.getItem('role');

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  useEffect(() => {
    const handleAuthChange = () => {
      setRole(localStorage.getItem('role'));
    };

    // Listen for the event we created in Login.js
    window.addEventListener("authChange", handleAuthChange);
    
    return () => window.removeEventListener("authChange", handleAuthChange);
  }, []);

  return (
    <nav style={styles.nav}>
      <h2>ðŸ“š LMS</h2>
      <div>
        {token ? (
          // Links visible only when LOGGED IN
          <>
            <Link to="/books" style={styles.link}>Books</Link>
            <Link to="/profile" style={styles.link}>My Loans</Link>
            {role === 'admin' && (
              <Link to="/admin-dashboard" style={styles.link}>Admin Panel</Link>
            )}
            <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
          </>
        ) : (
          // Links visible only when LOGGED OUT
          <>
            <Link to="/login" style={styles.link}>Login</Link>
            <Link to="/register" style={styles.link}>Register</Link>
          </>
        )}
        {role === 'admin' && (
        <Link to="/admin/logs" style={{ color: 'yellow', marginLeft: '15px' }}>
          Admin Logs
        </Link>
      )}
      </div>
    </nav>
  );
};

const styles = {
  nav: { 
    display: 'flex', 
    justifyContent: 'space-between', 
    padding: '1rem', 
    background: '#333', 
    color: '#fff',
    alignItems: 'center'
  },
  link: { 
    color: '#fff', 
    margin: '0 10px', 
    textDecoration: 'none',
    fontWeight: 'bold'
  },
  logoutBtn: { 
    background: '#dc3545', 
    color: 'white', 
    border: 'none', 
    cursor: 'pointer', 
    padding: '5px 12px',
    borderRadius: '4px',
    marginLeft: '10px'
  }
};

export default Navbar;
