import React, { useEffect, useState } from 'react';
import api from '../services/api';

const AdminLogs = () => {
    const [logs, setLogs] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchLogs = async () => {
            try {
                const res = await api.get('/admin/transactions');
                setLogs(res.data);
            } catch (err) {
                console.error("Error fetching logs");
            } finally {
                setLoading(false);
            }
        };
        fetchLogs();
    }, []);

    // Logic to filter logs based on search input
// Logic to filter logs based on search input
const filteredLogs = logs.filter(log => {
    // If the search bar is empty, show everything immediately
    if (searchTerm === "") return true;

    // We check multiple possible keys (with and without underscores) 
    // to make sure we catch the data regardless of your SQL column names
    const studentName = (log.username || log.user_name || "").toLowerCase();
    const bookName = (log.book_name || log.title || "").toLowerCase();
    const status = (log.status || "").toLowerCase();
    const search = searchTerm.toLowerCase();

    return studentName.includes(search) || 
           bookName.includes(search) || 
           status.includes(search);
});

    return (
        <div style={styles.container}>
            <div style={styles.headerRow}>
                <h2 style={styles.title}>üìú Transaction History</h2>
                
                {/* Search Bar */}
                <div style={styles.searchBox}>
                    <span style={styles.searchIcon}>üîç</span>
                    <input 
                        type="text" 
                        placeholder="Search by student, book, or status..." 
                        style={styles.searchInput}
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>
            </div>

            {loading ? (
                <p>Loading logs...</p>
            ) : (
                <div style={styles.tableCard}>
                    <table style={styles.table}>
                        <thead>
                            <tr style={styles.thRow}>
                                <th style={styles.th}>Student</th>
                                <th style={styles.th}>Book Title</th>
                                <th style={styles.th}>Issue Date</th>
                                <th style={styles.th}>Return Date</th>
                                <th style={styles.th}>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredLogs.length > 0 ? (
                                filteredLogs.map((log) => (
                                    <tr key={log.id} style={styles.tr}>
                                        <td style={styles.td}><strong>{log.username}</strong></td>
                                        <td style={styles.td}>{log.book_name}</td>
                                        <td style={styles.td}>{new Date(log.issue_date).toLocaleDateString()}</td>
                                        <td style={styles.td}>
                                            {log.return_date 
                                                ? new Date(log.return_date).toLocaleDateString() 
                                                : <span style={{color: '#95a5a6'}}>Pending</span>
                                            }
                                        </td>
                                        <td style={styles.td}>
                                            <span style={log.status === 'issued' ? styles.statusIssued : styles.statusReturned}>
                                                {log.status.toUpperCase()}
                                            </span>
                                        </td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="5" style={{padding: '40px', textAlign: 'center', color: '#7f8c8d'}}>
                                        No matching transactions found.
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

const styles = {
    container: { animation: 'fadeIn 0.5s ease' },
    headerRow: { 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '20px',
        flexWrap: 'wrap',
        gap: '15px'
    },
    title: { color: '#2c3e50', margin: 0 },
    searchBox: { 
        position: 'relative', 
        display: 'flex', 
        alignItems: 'center',
        width: '350px' 
    },
    searchIcon: { 
        position: 'absolute', 
        left: '12px', 
        color: '#95a5a6' 
    },
    searchInput: { 
        width: '100%', 
        padding: '10px 10px 10px 35px', 
        borderRadius: '8px', 
        border: '1px solid #ddd', 
        outline: 'none',
        fontSize: '14px'
    },
    tableCard: { 
        background: 'white', 
        borderRadius: '12px', 
        boxShadow: '0 4px 15px rgba(0,0,0,0.05)', 
        overflow: 'hidden' 
    },
    table: { width: '100%', borderCollapse: 'collapse' },
    thRow: { background: '#f8f9fa' },
    th: { padding: '15px', textAlign: 'left', color: '#4a5568', fontSize: '13px', fontWeight: '600' },
    td: { padding: '15px', borderBottom: '1px solid #edf2f7', fontSize: '14px', color: '#2d3436' },
    statusIssued: { 
        background: '#fff5f5', color: '#e53e3e', padding: '4px 10px', 
        borderRadius: '12px', fontSize: '11px', fontWeight: 'bold' 
    },
    statusReturned: { 
        background: '#f0fff4', color: '#38a169', padding: '4px 10px', 
        borderRadius: '12px', fontSize: '11px', fontWeight: 'bold' 
    }
};

export default AdminLogs;
