import React, { useEffect, useState } from 'react';
import BookCard from '../components/BookCard';
import api from '../services/api'; // You'll define this next

// frontend/src/pages/BookList.js
const BookList = () => {
    const [books, setBooks] = useState([]);

    // 1. Define the update function here
    const updateBookStock = (bookId) => {
        setBooks(prevBooks => 
            prevBooks.map(book => 
                book.id === bookId 
                ? { ...book, available_count: book.available_count - 1 } 
                : book
            )
        );
    };


    useEffect(() => {
    const fetchBooks = async () => {
        try {
            const res = await api.get('/books');
            console.log("API Response:", res.data); // Should be an array of objects
            setBooks(res.data);
        } catch (err) {
            console.error("Fetch error:", err);
        }
    };
    fetchBooks();
}, []);
    return (
        <div className="book-grid">
            {books.map(book => (
                <BookCard 
                    key={book.id} 
                    book={book} 
                    onBorrowSuccess={() => updateBookStock(book.id)} // 2. Pass it down
                />
            ))}
        </div>
    );
};
export default BookList;
