// frontend/src/pages/BookList.js
import React, { useEffect, useState } from 'react';
import BookCard from '../components/BookCard';
// import { getAllBooks } from '../services/bookService'; // You'll define this next
import api from '../services/api';
import { bookService } from '../services/bookService';

const styles = {
    container: { padding: '20px', maxWidth: '1200px', margin: '0 auto' },
    searchWrapper: { marginBottom: '30px', textAlign: 'center' },
    searchInput: {
        width: '80%',
        maxWidth: '500px',
        padding: '12px 20px',
        borderRadius: '25px',
        border: '1px solid #ddd',
        fontSize: '1rem',
        boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
        outline: 'none'
    },
    grid: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        gap: '20px'
    }
};

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

// Filter the books array based on search input
const filteredBooks = books.filter(book => 
    book.book_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    book.author.toLowerCase().includes(searchTerm.toLowerCase()) ||
    book.isbn?.includes(searchTerm)
);



const fetchBooks = useCallback(async () => {
    const data = await bookService.getAllBooks();
    setBooks(data);
}, []);


  useEffect(() => {
        fetchBooks();
    }, [fetchBooks]);

  if (loading) return <h2>Loading the library...</h2>;

return (
    <div>
        <div style={styles.searchContainer}>
            <input 
                type="text" 
                placeholder="Search by Title, Author, or ISBN..." 
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={styles.searchInput}
            />
        </div>
        
        <div className="book-grid">
            {filteredBooks.map(book => (
                <BookCard key={book.id} book={book} onBorrowSuccess={fetchBooks} />
            ))}
        </div>
    </div>
);



  // return (
  //   <div style={{ padding: '2rem' }}>
  //     <h1>Available Books</h1>
  //     <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)' }}>
  //       {books.map(book => (
  //         <BookCard key={book.id} book={book} />
  //       ))}
  //     </div>
  //   </div>
  // );
};

export default BookList;
