import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ManageBooks = () => {
    const [books, setBooks] = useState([]);

    const fetchBooks = async () => {
        const res = await axios.get('http://localhost:5000/api/books');
        setBooks(res.data);
    };

    useEffect(() => { fetchBooks(); }, []);

    const handleUpdateStock = async (id, newCount) => {
        try {
            await axios.put(`http://localhost:5000/api/admin/update-book/${id}`, { available_copies: newCount }, {
                headers: { 'x-auth-token': localStorage.getItem('token') }
            });
            fetchBooks(); // Refresh list
            alert("Stock updated!");
        } catch (err) { alert("Update failed"); }
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this book?")) {
            try {
                await axios.delete(`http://localhost:5000/api/admin/delete-book/${id}`, {
                    headers: { 'x-auth-token': localStorage.getItem('token') }
                });
                setBooks(books.filter(b => b.id !== id));
                alert("Book removed.");
            } catch (err) { alert("Cannot delete book (it might be currently borrowed)"); }
        }
    };

    return (
        <div style={styles.container}>
            <h2 style={{color: '#2c3e50'}}>ðŸ“‹ Inventory Management</h2>
            <div style={styles.tableCard}>
                <table style={styles.table}>
                    <thead>
                        <tr style={styles.thRow}>
                            <th style={styles.th}>Book Title</th>
                            <th style={styles.th}>Current Stock</th>
                            <th style={styles.th}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {books.map(book => (
                            <tr key={book.id} style={styles.tr}>
                                <td style={styles.td}><strong>{book.book_name}</strong></td>
                                <td style={styles.td}>
                                    <input 
                                        type="number" 
                                        defaultValue={book.available_copies} 
                                        style={styles.stockInput}
                                        onBlur={(e) => handleUpdateStock(book.id, e.target.value)}
                                    />
                                </td>
                                <td style={styles.td}>
                                    <button onClick={() => handleDelete(book.id)} style={styles.deleteBtn}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

const styles = {
    container: { animation: 'fadeIn 0.5s ease' },
    tableCard: { backgroundColor: 'white', borderRadius: '15px', overflow: 'hidden', boxShadow: '0 10px 30px rgba(0,0,0,0.05)' },
    table: { width: '100%', borderCollapse: 'collapse' },
    th: { padding: '15px', textAlign: 'left', backgroundColor: '#f8f9fa', color: '#34495e' },
    td: { padding: '15px', borderBottom: '1px solid #eee' },
    stockInput: { width: '60px', padding: '5px', borderRadius: '5px', border: '1px solid #ddd' },
    deleteBtn: { padding: '6px 12px', backgroundColor: '#e74c3c', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer' }
};

export default ManageBooks;
