import React, { useState } from 'react';
import axios from 'axios';

const AddBookForm = () => {
    const [book, setBook] = useState({ title: '', author: '', isbn: '', copies: 1 });
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            await axios.post('http://localhost:5000/api/admin/add-book', book, {
                headers: { 'x-auth-token': localStorage.getItem('token') }
            });
            alert("‚ú® Book added successfully to the collection!");
            setBook({ title: '', author: '', isbn: '', copies: 1 });
        } catch (err) {
            alert("‚ùå Error adding book. Please check your connection.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.card}>
            <div style={styles.header}>
                <h2 style={styles.title}>üìñ Catalog New Book</h2>
                <p style={styles.subtitle}>Fill in the details to update the library inventory</p>
            </div>
            
            <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.inputGroup}>
                    <label style={styles.label}>Book Title</label>
                    <input 
                        style={styles.input}
                        type="text" 
                        placeholder="e.g. The Great Gatsby" 
                        value={book.title} 
                        onChange={(e) => setBook({...book, title: e.target.value})} 
                        required 
                    />
                </div>

                <div style={styles.inputGroup}>
                    <label style={styles.label}>Author Name</label>
                    <input 
                        style={styles.input}
                        type="text" 
                        placeholder="e.g. F. Scott Fitzgerald" 
                        value={book.author} 
                        onChange={(e) => setBook({...book, author: e.target.value})} 
                        required 
                    />
                </div>

                <div style={{ display: 'flex', gap: '15px' }}>
                    <div style={{ ...styles.inputGroup, flex: 2 }}>
                        <label style={styles.label}>ISBN Number</label>
                        <input 
                            style={styles.input}
                            type="text" 
                            placeholder="Optional" 
                            value={book.isbn} 
                            onChange={(e) => setBook({...book, isbn: e.target.value})} 
                        />
                    </div>
                    <div style={{ ...styles.inputGroup, flex: 1 }}>
                        <label style={styles.label}>Copies</label>
                        <input 
                            style={styles.input}
                            type="number" 
                            min="1"
                            value={book.copies} 
                            onChange={(e) => setBook({...book, copies: e.target.value})} 
                            required 
                        />
                    </div>
                </div>

                <button 
                    type="submit" 
                    disabled={loading}
                    style={loading ? {...styles.button, opacity: 0.7} : styles.button}
                >
                    {loading ? 'Adding...' : '‚ûï Add to Collection'}
                </button>
            </form>
        </div>
    );
};

const styles = {
    card: {
        maxWidth: '550px',
        margin: '20px auto',
        background: '#ffffff',
        padding: '40px',
        borderRadius: '15px',
        boxShadow: '0 10px 25px rgba(0,0,0,0.05)',
        border: '1px solid #eaeaea'
    },
    header: {
        textAlign: 'center',
        marginBottom: '30px'
    },
    title: {
        margin: '0',
        color: '#2c3e50',
        fontSize: '24px',
        fontWeight: '700'
    },
    subtitle: {
        color: '#7f8c8d',
        fontSize: '14px',
        marginTop: '5px'
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        gap: '20px'
    },
    inputGroup: {
        display: 'flex',
        flexDirection: 'column',
        gap: '8px'
    },
    label: {
        fontSize: '14px',
        fontWeight: '600',
        color: '#34495e',
        paddingLeft: '5px'
    },
    input: {
        padding: '12px 15px',
        borderRadius: '8px',
        border: '1px solid #dcdde1',
        fontSize: '15px',
        outline: 'none',
        transition: 'border-color 0.3s ease',
        backgroundColor: '#f9f9f9'
    },
    button: {
        marginTop: '10px',
        backgroundColor: '#3498db',
        color: 'white',
        padding: '14px',
        border: 'none',
        borderRadius: '8px',
        fontSize: '16px',
        fontWeight: 'bold',
        cursor: 'pointer',
        transition: 'background 0.3s ease, transform 0.2s ease',
        boxShadow: '0 4px 6px rgba(52, 152, 219, 0.2)'
    }
};

export default AddBookForm;
