import React, { useState } from 'react';
import Dashboard from './AdminDashboard';
import AdminLogs from './AdminLogs';
import AddBookForm from './AddBookForm'; // We will create this below

const AdminDashboard = () => {
    const [activeTab, setActiveTab] = useState('dashboard');

    return (
        <div style={styles.container}>
            <aside style={styles.sidebar}>
                <h2 style={{color: 'white', textAlign: 'center'}}>LMS Admin</h2>
                <button style={activeTab === 'dashboard' ? styles.activeBtn : styles.btn} onClick={() => setActiveTab('dashboard')}>ðŸ“Š Dashboard</button>
                <button style={activeTab === 'logs' ? styles.activeBtn : styles.btn} onClick={() => setActiveTab('logs')}>ðŸ“œ System Logs</button>
                <button style={activeTab === 'add' ? styles.activeBtn : styles.btn} onClick={() => setActiveTab('add')}>âž• Add New Book</button>
            </aside>

            <main style={styles.content}>
                {activeTab === 'dashboard' && <AdminDashboard />}
                {activeTab === 'logs' && <AdminLogs />}
                {activeTab === 'add' && <AddBookForm />}
            </main>
        </div>
    );
};

const styles = {
    container: { display: 'flex', minHeight: '100vh', backgroundColor: '#f4f7f6' },
    sidebar: { width: '250px', backgroundColor: '#2c3e50', padding: '20px', display: 'flex', flexDirection: 'column', gap: '10px' },
    content: { flex: 1, padding: '30px' },
    btn: { padding: '12px', cursor: 'pointer', backgroundColor: 'transparent', color: '#bdc3c7', border: 'none', textAlign: 'left', borderRadius: '5px', fontSize: '16px' },
    activeBtn: { padding: '12px', cursor: 'pointer', backgroundColor: '#3498db', color: 'white', border: 'none', textAlign: 'left', borderRadius: '5px', fontSize: '16px' }
};

export default AdminDashboard;
